			if($sql_duplicate->num_rows>0)							{							}							else							{								$query_insert = "INSERT INTO subscribers SET email='".$email."',name='".$name."',date=now()";								if($sql_insert = $conn->prepare($query_insert))								{									$sql_insert->execute();								}							}						}					}					$_SESSION['response'] = $pagename." Added Successfully.";				}			}			echo "<script>document.location.href='users_mgmt.php';</script>";			exit;		}	}}/* Listing  */if($id!=""){    $query_select="SELECT * FROM users WHERE id='".$id."'";	if($sql_select=$conn->query($query_select))	{ 		if($sql_select->num_rows>0)		{			$result=$sql_select->fetch_array(MYSQLI_ASSOC);			$email = stripslashes($result['email']);			$name = stripslashes($result['fname']);			$email = $result['email'];			$phone_no = $result['phone_no'];			$newsletter = $result['newsletter'];			$pagetaskname = " Update ";		}    }}?><!DOCTYPE HTML><html><head><?php include('includes/head.php'); ?></head><body><!-- header section start here --><?php include('includes/header.php'); ?><!-- end here --> <!-- Two section start here --><div class="container-fluid">	<div class="left-section">	<?php include('includes/customer-left.php'); ?>		</div>	<div class="right-section">		<div class="dashboard-bar">		<h2 class="headn-h2">Customers</h2>		</div>		<div class="main-section">			<div class="col-sm-12 col-md-6 col-lg-offset-3 col-xs-12">				<div class="panel panel-default">					<?php if($msg!="") { ?> 					<div class="alert alert-danger fade in f">						<a href="#" class="close" data-dismiss="alert">&times;</a>						<?php echo $msg; ?>					</div>					<?php } ?>					<div class="panel-heading">					<h3 class="panel-title">Users</h3>					</div>					<div class="panel-body" style="padding:30px;">						<form method="post" name="<?php echo strtolower($pagename); ?>" id="users" enctype="multipart/form-data">							<div class="form-group">								<label>Name</label>